    `created_by`       int(11)     NOT NULL COMMENT 'references users.id for session owner',
    `created_time`     timestamp   NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT 'created time',
    KEY amendment_history_id (`amendment_id`)
) ENGINE = InnoDB;

